#include <iostream>
#include <vector>
#include <unordered_map>
#include <sstream>
#include <string>
using namespace std;

int main() {
    string line;
    getline(cin, line);
    istringstream iss(line);
    vector<int> nums;
    int num;
    while (iss >> num) nums.push_back(num);

    int target;
    cin >> target;

    unordered_map<int, int> lookup;
    vector<int> result;
    for (int i = 0; i < nums.size(); ++i) {
        int complement = target - nums[i];
        if (lookup.find(complement) != lookup.end()) {
            result.push_back(lookup[complement]);
            result.push_back(i);
            break;
        }
        lookup[nums[i]] = i;
    }

    cout << "[" << result[0] << "," << result[1] << "]" << endl;
    return 0;
}

